import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:description:GetAlertInformationResponder:2'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:description:2'

def alerts = holder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetAlertInformationResponse/urn:alertInformation')
def offending = []

assert alerts.size() > 0

alerts.each { doc ->
    def alertHolder = new XmlHolder( doc )
    holder.namespaces.each {key, value -> alertHolder.namespaces[key] = value}

    def documentId = alertHolder.getNodeValue('/urn:alertInformation/urn1:alertInformationHeader/urn1:documentId')
    def validityTimeStart = alertHolder.getNodeValue('urn:alertInformation/urn1:alertInformationBody/urn1:validityTimePeriod/urn1:start')
    validityTimeStart = validityTimeStart.substring(0, 8)
    def validityTimeEnd = alertHolder.getNodeValue('urn:alertInformation/urn1:alertInformationBody/urn1:validityTimePeriod/urn1:end')
    validityTimeEnd = validityTimeEnd == null ? null : validityTimeEnd.substring(0, 8)
    def obsoleteTime = alertHolder.getNodeValue('/urn:alertInformation/urn1:alertInformationBody/urn1:obsoleteTime')
    obsoleteTime = obsoleteTime == null ? null : obsoleteTime.substring(0, 8)

    if (obsoleteTime != null && ((validityTimeEnd == null) || (obsoleteTime < validityTimeEnd))) {
        validityTimeEnd = obsoleteTime
    }

    if((validityTimeEnd == null && validityTimeStart > context.timePeriodEnd) ||
            ((validityTimeEnd != null) && !((validityTimeStart <= context.timePeriodStart && validityTimeEnd >= context.timePeriodStart) ||
                    (validityTimeEnd >= context.timePeriodEnd && validityTimeStart <= context.timePeriodEnd) ||
                    (validityTimeStart >= context.timePeriodStart && validityTimeEnd <= context.timePeriodEnd)))) {
        offending.add(documentId)
    }
}

if(!offending.isEmpty()) {
    log.error("DocumentIds $offending is not valid for the specified interval")
    assert(false)
}

import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:description:GetAlertInformationResponder:2'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:description:2'

def documentIds = holder.getNodeValues('/soapenv:Envelope/soapenv:Body/urn:GetAlertInformationResponse/urn:alertInformation/urn1:alertInformationHeader/urn1:documentId') as List

def expectedDocumentIds = context.expectedDocumentIds ? context.expectedDocumentIds.tokenize(',') : []
def unexpectedDocumentIds = context.unexpectedDocumentIds ? context.unexpectedDocumentIds.tokenize(',') : []

log.debug("Assert documentIds, unexpectedDocumentIds:$unexpectedDocumentIds,\nexpectedDocumentIds, $expectedDocumentIds,\nfound documentIds $documentIds")

def missingExpectedIds = []
def extraUnexpectedIds= []

documentIds.each {id ->
    if (unexpectedDocumentIds.contains(id)) {
        extraUnexpectedIds.add(id)
    }
}

expectedDocumentIds.each {id ->
    if (!documentIds.contains(id)) {
        missingExpectedIds.add(id)
    }
}

if(!missingExpectedIds.isEmpty() || !extraUnexpectedIds.isEmpty()) {
    log.error("Response contains unexpected DocumentIds: $extraUnexpectedIds and/or does not contain  expected DocumentIds: $missingExpectedIds")
    assert(false)
}

