import com.eviware.soapui.support.XmlHolder

def responseHolder = new XmlHolder( messageExchange.response.responseContent )
responseHolder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
responseHolder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:description:GetAlertInformationResponder:2'
responseHolder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:description:2'

def requestHolder =  new XmlHolder( messageExchange.request.requestContent )
requestHolder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
requestHolder.namespaces['urn'] = 'urn:riv:itintegration:registry:1'
requestHolder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:description:GetAlertInformationResponder:2'
requestHolder.namespaces['urn2'] = 'urn:riv:clinicalprocess:healthcond:description:2'

// This is so stupid, I get ${sourceSystemHSAId}, not the actual value
def requestSourceSystemHSAId = requestHolder.getNodeValue('/soapenv:Envelope/soapenv:Body/urn1:GetAlertInformation/urn1:sourceSystemHSAId')
def requestCareContactId = requestHolder.getNodeValue('/soapenv:Envelope/soapenv:Body/urn1:GetAlertInformation/urn1:careContactId[1]')
log.debug("requestSourceSystemHSAId: $requestSourceSystemHSAId")
log.debug("requestCareContactId: $requestCareContactId")

def alerts = responseHolder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetAlertInformationResponse/urn:alertInformation')

assert alerts.size() != 0

alerts.each {l ->
    def alertHolder = new XmlHolder( l )
    responseHolder.namespaces.each {key, value -> alertHolder.namespaces[key] = value}

    def sourceSystemHSAId = alertHolder.getNodeValue('/urn:alertInformation/urn1:alertInformationHeader/urn1:sourceSystemHSAId')
    def careContactId = alertHolder.getNodeValue('/urn:alertInformation/urn1:alertInformationHeader/urn1:careContactId')

    assert sourceSystemHSAId == context.sourceSystemHSAId
    if(requestCareContactId != null) {
        log.debug("asserting CareContactId")
        assert careContactId == context.careContactId1 || careContactId == context.careContactId2
    }
}

