con:configuration.scriptText:
import se.skl.rivta.testsupport.soapui.validation.SchematronValidator
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)
def validator = new SchematronValidator()

def failures = validator.validateMessage(messageExchange.response.getResponseContent(),
	new File(utils.projectPath + "/constraints.xml"))

failures.each { failure -> contextHelper.fail(failure.text) }

import static com.eviware.soapui.support.StringUtils.hasContent
import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:logistics:logistics:GetCareContactsResponder:2'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:logistics:logistics:2'

def careContacts = holder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetCareContactsResponse/urn:careContact')

careContacts.each {c -> 
	def contactHolder = new XmlHolder( c )
	holder.namespaces.each {key, value -> contactHolder.namespaces[key] = value}


	def careContactId = contactHolder.getNodeValue('/urn:careContact/urn1:careContactHeader/urn1:careContactId')
	def documentTime = contactHolder.getNodeValue('/urn:careContact/urn1:careContactHeader/urn1:documentTime')
	def datePeriodStart = contactHolder.getNodeValue('/urn:careContact/urn1:careContactBody/urn1:careContactTimePeriod/urn1:start')
	def datePeriodEnd = contactHolder.getNodeValue('/urn:careContact/urn1:careContactBody/urn1:careContactTimePeriod/urn1:end')

	if (context.timePeriodStart <= documentTime[0..7] && documentTime[0..7] <= context.timePeriodEnd) {
		return
	}
	if (datePeriodStart != null && datePeriodStart[0..7] > context.timePeriodEnd) {
		contextHelper.fail('Response contains a contact after end time. CareContactId: ' + careContactId)
	}
	if (datePeriodEnd != null && datePeriodEnd[0..7] < context.timePeriodStart) {
		contextHelper.fail('Response contains a contact before start time. CareContactId: ' + careContactId)
	}
}

import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:logistics:logistics:GetCareContactsResponder:2'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:logistics:logistics:2'

def careContactIds = holder.getNodeValues('/soapenv:Envelope/soapenv:Body/urn:GetCareContactsResponse/urn:careContact/urn1:careContactHeader/urn1:careContactId') as List

def expectedCareContactIds = context.expectedCareContactIds ? context.expectedCareContactIds.tokenize(',') : []
def unexpectedCareContactIds = context.unexpectedCareContactIds ? context.unexpectedCareContactIds.tokenize(',') : []

careContactIds.each {id -> 
	if (unexpectedCareContactIds.contains(id)) {
		contextHelper.fail('Response contains a unexpected CareContact. CareContactId: ' + id)
	}
}
expectedCareContactIds.each {id -> 
	if (!careContactIds.contains(id)) {
		contextHelper.fail('Response does not contain a expected CareContact. CareContactId: ' + id)
	}
}

