	def careContactId = contactHolder.getNodeValue('/urn:careContact/urn1:careContactHeader/urn1:careContactId')
	def careUnitHSAid = contactHolder.getNodeValue('/urn:careContact/urn1:careContactHeader/urn1:accountableHealthcareProfessional/urn1:healthcareProfessionalCareUnitHSAid')

	if (careUnitHSAid != context.careUnitHSAid) {
		contextHelper.fail("Response contains a contact with other CareUnitHSAid. CareUnitHSAid: ${careUnitHSAid} CareContactId: ${careContactId}")
	}
}

