con:configuration.scriptText:
import se.skl.rivta.testsupport.soapui.validation.SchematronValidator
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)
def validator = new SchematronValidator()

def failures = validator.validateMessage(messageExchange.response.getResponseContent(),
	new File(utils.projectPath + "/constraints.xml"))

failures.each { failure -> contextHelper.fail(failure.text) }

import static com.eviware.soapui.support.StringUtils.hasContent
import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = context.expand('${#Project#urn}')
holder.namespaces['urn1'] = context.expand('${#Project#urn1}')

def careContacts = holder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetCareContactsResponse/urn:careContact')
assert careContacts.size() > 0 : "No careContacts returned"

careContacts.each {c -> 
	def contactHolder = new XmlHolder( c )
	holder.namespaces.each {key, value -> contactHolder.namespaces[key] = value}


	def documentId = contactHolder.getNodeValue('/urn:careContact/urn1:careContactHeader/urn1:documentId')
	def authorTime = contactHolder.getNodeValue('/urn:careContact/urn1:careContactHeader/urn1:accountableHealthcareProfessional/urn1:authorTime')
	def datePeriodStart = contactHolder.getNodeValue('/urn:careContact/urn1:careContactBody/urn1:careContactTimePeriod/urn1:start')
	def datePeriodEnd = contactHolder.getNodeValue('/urn:careContact/urn1:careContactBody/urn1:careContactTimePeriod/urn1:end')

	if (context.timePeriodStart <= authorTime[0..7] && authorTime[0..7] <= context.timePeriodEnd) {
		return
	}
	if (datePeriodStart != null && datePeriodStart[0..7] > context.timePeriodEnd) {
		contextHelper.fail('Response contains a contact after end time. DocumentId: ' + documentId)
	}
	if (datePeriodEnd != null && datePeriodEnd[0..7] < context.timePeriodStart) {
		contextHelper.fail('Response contains a contact before start time. DocumentId: ' + documentId)
	}
}

import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = context.expand('${#Project#urn}')
holder.namespaces['urn1'] = context.expand('${#Project#urn1}')


def documentIds = holder.getNodeValues('/soapenv:Envelope/soapenv:Body/urn:GetCareContactsResponse/urn:careContact/urn1:careContactHeader/urn1:documentId') as List
assert documentIds.size() > 0 : "No careContacts returned"

def expectedDocumentIds = context.expectedDocumentIds ? context.expectedDocumentIds.tokenize(',') : []
def unexpectedDocumentIds = context.unexpectedDocumentIds ? context.unexpectedDocumentIds.tokenize(',') : []

documentIds.each {id -> 
	if (unexpectedDocumentIds.contains(id)) {
		contextHelper.fail('Response contains a unexpected CareContact. DocumentId: ' + id)
	}
}
expectedDocumentIds.each {id -> 
	if (!documentIds.contains(id)) {
		contextHelper.fail('Response does not contain a expected CareContact. DocumentId: ' + id)
	}
}

